import React, { useEffect, useState } from "react";
import { Link, useParams, useHistory } from "react-router-dom";
import { readCard, updateCard, readDeck } from "../utils/api/index";

const EditCard = () => {
    const { deckId, cardId } = useParams();
    const history = useHistory();
    const [ deckName, setDeckName ] = useState("");
    const [ card, setCard ] = useState({
        id: cardId,
        front: "",
        back: "",
        deckId: deckId
    });

    useEffect(() => {
        async function loadCard() {
            const result = await readCard(cardId);
            setCard({
                id: cardId,
                front: result.front,
                back: result.back,
                deckId: deckId
            });
        }
        async function loadDeckName() {
            const deck = await readDeck(deckId);
            setDeckName(deck.name);
        }
        loadCard();
        loadDeckName();
    }, [cardId, deckId]);

    const cardFrontHandler = (event) => {
        setCard({ ...card, front: event.target.value });
    }

    const cardBackHandler = (event) => {
        setCard({ ...card, back: event.target.value });
    }

    const submitHandler = (event) => {
        event.preventDefault();
        updateCard(card).then((result) => history.push(`/decks/${result.deckId}`));
    }

    return (
        <div>
            <nav aria-label="breadcrumb">
              <ol className="breadcrumb">
                  <li className="breadcrumb-item">
                      <Link to={"/"} className="btn btn-link">Go Home</Link>
                  </li>
                  <li className="breadcrumb-item">
                      <Link to={`/decks/${deckId}`}>{deckName}</Link>
                  </li>
                  <li className="breadcrumb-item">
                      Edit Card
                  </li>
              </ol>
            </nav>
        <h4>Edit Deck</h4>
        <form onSubmit={submitHandler}>
          <div>
            <label>Question</label>
                <input
                    type="text"
                    onChange={cardFrontHandler}
                    defaultValue={card.front}
                />
          </div>
          <div>
            <label>Answer</label>
                <input
                    type="text"
                    onChange={cardBackHandler}
                    defaultValue={card.back}
                />
            </div>
            <button type="button" onClick={() => history.go(-1)}>Back to Deck</button>
            <button type="submit">Save</button>
        </form>
        </div>
    )
}

export default EditCard;
