import React, { useState, useEffect } from "react";
import { readDeck } from "../utils/api";
import { Link, useParams, useHistory } from "react-router-dom";

export default function Study() {
    const { deckId } = useParams();
    const [ deck, setDeck ] = useState({});
    const [ cards, setCards ] = useState({});
    const [ currentCard, setCurrentCard ] = useState(0);
    const [ font, setFront ] = useState(true)

    useEffect(() => {
        async function loadStudyDeck() {
            try {
                const result = await readDeck(deckId);
                setDeck(result);
                setCards(result.cards);
            }catch (error){
                console.log("Aborted Study Deck")
            }
        }
        loadStudyDeck();
    }, [deckId]);

    function nextCard() {
        setCurrentCard(currentCard+1)
    }

    function flipCard() {
        setFront(!front)
    }


    return (
        <div>
              <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item">
                            <Link to={"/"} className="btn btn-link">Go Home</Link>
                        </li>
                        <li className="breadcrumb-item">
                            <Link to={`/decks/${deckId}`} className="btn btn-link">{deck.name}</Link>
                        </li>
                        <li className="breadcrumb-item active" aria-current="page">
                            Study
                        </li>
                    </ol>
                </nav>
                <h4>Study: {deck.name}</h4>
                <div className="card">
                    <div className="card-body">
                        <h5>Card {currentCard+1} of {cards.length}</h5>
                        <p className="card-text">{flipCard ? `${cards[currentCard].front}` : `${cards[currentCard].back}`}</p>
                        <button className="btn btn-primary" onClick={flipCard}>
                            Flip
                        </button>
                        <button className="btn btn-secondary" onClick={nextCard}>
                            Next
                        </button>
                    </div>
                </div>
        </div>
    )
}
