import React from "react";
import { Link, useHistory } from "react-router-dom";
import { deleteDeck } from "../utils/api/index";

function DeckList({ decks }){
    const history = useHistory();

    const deleteDeckHandler = (deckId) => {
        if(window.confirm(`Are you sure you want to delete this deck? \n \nYou will not be able to recover it.`)){
            deleteDeck(deckId).then(history.go(0));
        }
    };

    const returnedList = decks.map((deck, index) => (
            <div className="card" key={index}>
                <div className="card-body">
                    <div className="row justify-content-between">
                        <h2 class="card-title">{deck.name}</h2>
                        <p>{deck.cards.length} cards</p>
                    </div>
                    <p class="card-text">{deck.description}</p>
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <Link to={`/decks/${deck.id}`} className="btn btn-secondary btn-lrg">View</Link>
                                <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">Study</Link>
                            </div>
                            <div className="col col-lg-2">
                            <button className="btn btn-danger" onClick={() => deleteDeckHandler(deck.id)}>Delete</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        ))

    return <ul>{returnedList}</ul>
}

export default DeckList;
