import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { createCard, readDeck } from "../utils/api/index"

const AddCard = () => {
    const { deckId } = useParams();
    const [ deck, setDeck ] = useState({});
    const [ cardFront, setCardFront ] = useState("");
    const [ cardBack, setCardBack ] = useState("");

    useEffect(() => {
        async function loadDecks() {
            const result = await readDeck(deckId);
            setDeck(result);     
        }
        loadDecks();
    }, [deckId]);

    const cardFrontHandler = (event) => {
        setCardFront(event.target.value);
    }

    const cardBackHandler = (event) => {
        setCardBack(event.target.value);
    }

    const submitHandler = (event) => {
        event.preventDefault();

    async function updateCardData() {
        const cardData = {
            front: cardFront,
            back: cardBack,
            deckId,
        }
        await createCard(deck.id, cardData);
        setCardFront("");
        setCardBack("")
        }
        updateCardData();
    }

    return (
        <div>
            <div>
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item">
                            <Link to={"/"} className="btn btn-link">Go Home</Link>
                        </li>
                        <li className="breadcrumb-item active" aria-current="page">
                            Add Card
                        </li>
                    </ol>
                </nav>
            </div>
            <h4>{deck.name}: Add Card</h4>
            <form onSubmit={submitHandler}>
                <div>
                <label>Question</label>
                    <input
                        type="text"
                        value={cardFront}
                        onChange={cardFrontHandler}
                    />
                </div>
                <div>
                <label>Answer</label>
                    <input
                        type="text"
                        value={cardBack}
                        onChange={cardBackHandler}
                    />
                </div>
                <button type="submit">Add Card</button>
            </form>
        </div>

    )
}

export default AddCard;
