import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { deleteDeck, readDeck } from "../utils/api/index";

export default function Deck({ deck }) {

    const deckId = deck.id;

    // useEffect(() => {
    //     async function loadDeck() {
    //         try {
    //             const responseFromAPI = await readDeck(deckId);
    //             setDecks(responseFromAPI);
    //         }catch (error){
    //             console.log("Aborted Deck")
    //         }
    //     }
    //     loadDeck();
    // }, [deckId])


    const handleDelete = async ({ target }) => {
        const value = target.value;

        const result = window.confirm(`Delete this deck? \n \nYou will not be able to recover it.`);
        if(result) {
            async function deleteThisDeck() {
                try {
                    await deleteDeck(value);
                    // const response = await listDecks();
                    // setDecks(response);
                }catch (error){
                    console.log("Aborted Delete")
                }
            }
        deleteThisDeck();
        }
    }

    const deckStyle = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'gainsboro',
        padding: '2rem',
        borderRadius: '.50rem',
        margin: "1rem 150px 3rem 150px",
        boxShadow: '0 0 4px 1px'
    }

    return (
        <div className="card" style={deckStyle}>
            <div className="col">
                <div className="row justify-content-between">
                    <h2>{deck.name}</h2>
                    <p>{deck.cards.length} cards</p>
                </div>
                <div>
                    <p>{deck.description}</p>
                </div>
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <Link to="/decks/:deckId/edit" className="btn btn-secondary">View</Link>
                            <Link to="/decks/:deckId/study" className="btn btn-primary">Study</Link>
                        </div>
                        <div className="col" style={{textAlign: "right"}}>
                        <button className="btn btn-danger" onClick={handleDelete}>Delete</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
