import React from "react";
import { Link, useHistory } from "react-router-dom";
import { deleteDeck } from "../utils/api/index";

const DeckList = ({ decks }) => {
    const history = useHistory();

    const deleteDeckHandler = () => {
        const result = window.confirm(`Delete this deck? \n \nYou will not be able to recover it.`);
        if(result) {
            async function deleteThisDeck() {
                try {
                    await deleteDeck(deckId);
                    history.push("/");
                }catch (error){
                    console.log("Aborted Deck Delete")
                }
            }
        deleteThisDeck();
        }
    };

    return (
        <ul>
        {decks.map((deck) => (
             <div className="card" >
                <div className="container">
                    <div className="row justify-content-between">
                        <h2>{deck.name}</h2>
                        <p>{deck.cards.length} cards</p>
                    </div>
                    <div className="card-body">
                        <p>{deck.description}</p>
                    </div>
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <Link to={`/decks/${deck.id}`} className="btn btn-secondary">View</Link>
                                <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">Study</Link>
                            </div>
                            <div className="col" style={{textAlign: "right"}}>
                            <button className="btn btn-danger" onClick={deleteDeckHandler}>Delete</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        ))}
        </ul>
    )
}

export default DeckList;
