export default function DeckForm({
    changeName,
    changeDescription,
    deck = {}
}) {

    return (
        <div>
            <label htmlFor="deckName">
                Name
            </label>
                <input 
                    type="text" 
                    id="deckName" 
                    name="name" 
                    placeholder="Deck Name"  
                    value={deck.name}
                    onChange={changeName}
                />
        <br />
        <br />
            <label htmlFor="description">
                Description
            </label>
                <input 
                    className="col" 
                    type="text" 
                    id="description" 
                    name="description" 
                    placeholder="Brief Description of the Deck"
                    value={deck.description}
                    onChange={changeDescription}
                />
        </div>
    )
}
