import React, { useState, useEffect } from "react";
import { readDeck } from "../utils/api";
import { Link, useParams, useHistory } from "react-router-dom";

const Study = () => {
    const { deckId } = useParams();
    const history = useHistory();
    const [ studyDeck, setStudyDeck ] = useState({})
    const [ studyCards, setStudyCards ] = useState({})
    const [ cardIndex, setCardIndex ] = useState(0);
    const [ flip, setFlip ] = useState(true);

    useEffect(() => {
        async function loadStudyDeck() {
            try {
                const result = await readDeck(deckId);
                setStudyDeck(result);
                setStudyCards(result.cards);
            }catch (error){
                console.log("Aborted Study Deck")
            }
        }
        loadStudyDeck();
    }, [deckId]);

    const toggleFlip = () => {
        setFlip(!flip)
    }

    return (
        <div>
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item">
                        <Link to={"/"} className="btn btn-link">Go Home</Link>
                    </li>
                    <li className="breadcrumb-item">
                        <Link to={`/decks/${deckId}`} className="btn btn-link">{studyDeck.name}</Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page">
                        Study
                    </li>
                </ol>
            </nav>
            <h4>Study: {studyDeck.name}</h4>
                <div className="card">
                    <div className="card-body">
                        <h5>Card #1</h5>
                        <p className="card-text"></p>
                        <button className="btn btn-primary" onClick={toggleFlip}>
                            Flip
                        </button>
                        <button className="btn btn-secondary">
                            Next
                        </button>
                    </div>
                </div>
        </div>
    )
}

export default Study;


