import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { listDecks } from "../utils/api";
import Deck from "./Deck";

export default function DeckList({ decks }) {

    // useEffect(() => {
    //     const abortController = new AbortController();

    //     async function loadDecks() {
    //         try {
    //             const result = await listDecks();
    //             setDecks(result);
    //         }catch (error){
    //             console.log("Aborted Deck")
    //         }
    //     }
    //     loadDecks();
    //     return () => abortController.abort();
    // }, []);

    const handleDelete = async ({ target }) => {
        const value = target.value;

        const result = window.confirm(`Delete this deck? \n \nYou will not be able to recover it.`);
        if(result) {
            async function deleteThisDeck() {
                try {
                    await deleteDeck(value);
                    // const response = await listDecks();
                    // setDecks(response);
                }catch (error){
                    console.log("Aborted Delete")
                }
            }
        deleteThisDeck();
        }
    }

    const mappedDecks = decks.map((deck) => (
        <div className="card" style={deckStyle}>
            <div className="col">
                <div className="row justify-content-between">
                    <h2>{deck.name}</h2>
                    <p>{deck.cards.length} cards</p>
                </div>
                <div>
                    <p>{deck.description}</p>
                </div>
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <Link to={`/decks/${deck.id}/edit`} className="btn btn-secondary">View</Link>
                            <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">Study</Link>
                        </div>
                        <div className="col" style={{textAlign: "right"}}>
                        <button className="btn btn-danger" onClick={handleDelete}>Delete</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        ))

    const deckStyle = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'gainsboro',
        padding: '2rem',
        borderRadius: '.50rem',
        margin: "1rem 150px 3rem 150px",
        boxShadow: '0 0 4px 1px'
    }

    return (
        <div>
            {mappedDecks}
        </div>
    )
}
