import React from "react";
import { Link } from "react-router-dom";
import { deleteDeck, listDecks } from "../utils/api";

export default function Deck({ deck, setDecks }) {

    const handleDelete = async ({ target }) => {
        const value = target.value;

        const result = window.confirm(`Delete this deck? \n \nYou will not be able to recover it.`);
        if(result) {
            async function deleteThisDeck() {
                try {
                    await deleteDeck(value);
                    const response = await listDecks();
                    setDecks(response);
                }catch (error){
                    console.log("Aborted Delete")
                }
            }
        deleteThisDeck();
        }
    }

    return (
        <div className="deck">
            <div className="col">
                <h2>{deck.name}</h2>

                <p>{deck.description}</p>

                <div className="container">
                    <div className="row">
                        <div className="col-8">
                            <Link to="./Home" className="btn btn-secondary">View</Link>
                            <Link to="/decks/:deckId/study" className="btn btn-primary">Study</Link>
                        </div>
                        <div className="col-2">
                        <button className="btn btn-danger" onClick={handleDelete}>Delete</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
