import React from "react";
import { deleteDeck } from "../utils/api";
import { Link, useHistory } from "react-router-dom";

export default function DeckList({ decks }) {

    const history = useHistory();

    function deleteDeckHandler(deckId) {
        const result = window.confirm(`Delete this deck? \n \nYou will not be able to recover it.`);
        if(result) {
            async function deleteThisDeck() {
                try {
                    await deleteDeck(deckId);
                    history.push("/");
                }catch (error){
                    console.log("Aborted Deck Delete")
                }
            }
        deleteThisDeck();
        }
    };

    const deckStyle = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'gainsboro',
        padding: '2rem',
        borderRadius: '.50rem',
        margin: "1rem 150px 3rem 150px",
        boxShadow: '0 0 4px 1px'
    }


    return (
        decks.map((deck) => (
                <div className="card" style={deckStyle}>
                <div className="container">
                    <div className="row justify-content-between">
                        <h2>{deck.name}</h2>
                        <p>{deck.cards.length} cards</p>
                    </div>
                    <div className="card-body">
                        <p>{deck.description}</p>
                    </div>
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <Link to={`/decks/${deck.id}`} className="btn btn-secondary">View</Link>
                                <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">Study</Link>
                            </div>
                            <div className="col" style={{textAlign: "right"}}>
                            <button className="btn btn-danger" onClick={() => deleteDeckHandler(deck.id)}>Delete</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        ))
        )
    }
