import React from "react";
import { Link, useHistory } from "react-router-dom";
import { deleteDeck } from "../utils/api/index";

const DeckList = ({ decks }) => {
    const history = useHistory();

    const deleteDeckHandler = (deckId) => {
        if(window.confirm(`Are you sure you want to delete this deck? \n \nYou will not be able to recover it.`)){
            deleteDeck(deckId);
        }
    };

    const renderList = decks.map((deck, index) => (
             <div className="card" key={index}>
                <div className="container">
                    <div className="row justify-content-between">
                        <h2>{deck.name}</h2>
                        <p>{deck.cards.length} cards</p>
                    </div>
                    <div className="card-body">
                        <p>{deck.description}</p>
                    </div>
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <Link to={`/decks/${deck.id}`} className="btn btn-secondary">View</Link>
                                <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">Study</Link>
                            </div>
                            <div className="col" style={{textAlign: "right"}}>
                            <button className="btn btn-danger" onClick={deleteDeckHandler}>Delete</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        ))

    return <ul>{renderList}</ul>
}

export default DeckList;
