    return(
        <form onSubmit={submitHandler}>
          <div>
            <label>Question</label>
                <input
                    type="text"
                    onChange={cardFrontHandler}
                    defaultValue={card.front}
                />
          </div>
          <div>
            <label>Answer</label>
                <input
                    type="text"
                    onChange={cardBackHandler}
                    defaultValue={card.back}
                />
            </div>
            <button type="submit">Save</button>
        </form>
    )
}

export default CardForm;
