import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom"
import { createDeck } from "../utils/api"

const CreateDeck = () => {

    const newDeckForm = {
        name: "",
        description: ""
    }

    const handleChange = ({target}) => {
        const value = target.value;
        setNewDeck({
            ...newDeck,
            [target.name]: value,
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        async function updateDecks() {
            try {
                const result = await createDeck(newDeck);
                history.push(`/decks/${result.id}`);
            }catch (error){
                console.log("Aborted Deck")
            }
        }
        updateDecks();
    };

    return (
        <div>
          <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item">
                        <Link to={"/"} className="btn btn-link">Go Home</Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page" key="1">Create Deck</li>
                </ol>
          </nav>
        <h1>Create Deck</h1>
        <form onSubmit={handleSubmit}>
            <div>
                <DeckForm />
            </div>
            <button onClick={() => history.push("/")}>Cancel</button>
            <button className="btn btn-primary" type="submit">Submit</button>
        </form>
        </div>
    )
}

export default CreateDeck;
