import React, { useState, useEffect } from "react";
import { readDeck } from "../utils/api";
import { Link, useParams, useHistory } from "react-router-dom";

export default function Study({ decks }) {
    const { deckId } = useParams();
    const history = useHistory();
    const [ cardInfo, setCardInfo ] = useState({
        id: 0,
        front: "",
        back: "",
        cardMax: 0,
    })

    const [ deck, setDeck ] = useState({
        id: 0,
        name: "",
        description: "",
    });

    useEffect(() => {
        async function loadStudyDeck() {
            try {
                const result = await readDeck(deckId);
                setDeck(result);
                setCardInfo({
                    id: (deckId),
                    front: "",
                    back: "",
                    cardMax: deckId.length
                })
            }catch (error){
                console.log("Aborted Study Deck")
            }
        }
        loadStudyDeck();
    }, [deckId]);

    const studyCards = () => {}



    return (
        <div>
              <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item">
                            <Link to={"/"} className="btn btn-link">Go Home</Link>
                        </li>
                        <li className="breadcrumb-item">
                            <Link to={`/decks/${deckId}`} className="btn btn-link">{deck.name}</Link>
                        </li>
                        <li className="breadcrumb-item active" aria-current="page">
                            Study
                        </li>
                    </ol>
                </nav>
                <h4>Study: {deck.name}</h4>
                <div className="card">
                    <div className="card-body">
                        <h5>Card</h5>
                        <p className="card-text"> </p>
                        <button className="btn btn-primary" >
                            Flip
                        </button>
                        <button className="btn btn-secondary">
                            Next
                        </button>
                    </div>
                </div>
        </div>
    )
}
