import React, { useState } from "react";
import { Link } from "react-router-dom";
import { deleteDeck, listDecks } from "../utils/api";
import { setDecks } from "./DeckList"

export default function Deck({ deck, setDecks }) {

    const handleDelete = async ({ target }) => {
        const value = target.value;

        const result = window.confirm(`Delete deck ID ${value}? You will not be able to recover it.`);
        if(result) {
            async function deleteThisDeck() {
                try {
                    await deleteDeck(value);
                    const response = await listDecks();
                    setDecks(response);
                }catch (error){
                    console.log("Aborted Deck")
                }
            }
        deleteThisDeck();
        }
    }

    return (
        <div  className="card-grid ">
            <h2>{deck.name}</h2>
            <p>{deck.description}</p>

            <Link to="./Home">
                <button className="btn btn-secondary ">View</button>
            </Link>
            <Link to="/decks/:deckId/study">
                <button className="btn btn-primary">Study</button>
            </Link>

            <button className="btn btn-danger" onClick={handleDelete}>Delete</button>

        </div>
    )
}
