import React, { useEffect, useState } from "react";
import { Link, useParams, useHistory } from "react-router-dom";
import { readDeck, updateDeck } from "../utils/api"
import DeckForm from "./DeckForm"

export default function DeckEdit({ setDecks }) {
    const { deckId } = useParams();
    const history = useHistory();
    const [ deck, setDeck ] = useState({
        id: 0,
        name: "",
        description: "",
    });

    useEffect(() => {
        async function loadDecks() {
            try {
                const result = await readDeck(deckId);
                setDeck(result);
            }catch (error){
                console.log("Aborted Edit Deck")
            }
        }
        loadDecks();
    }, [deckId]);

    function changeName(event) {
        setDeck({...deck, name: event.target.value});
    }

    function changeDescription(event) {
        setDeck({...deck, description: event.target.value})
    }

    function handleSubmit(event) {
        event.preventDefault();
        updateDeck(deck).then((result) => history.push(`/decks/${result.id}`));
    }

    return (
        <div>
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item">
                        <Link to={"/"} className="btn btn-link">Go Home</Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page">
                        Edit Deck
                    </li>
                </ol>
            </nav>
            <DeckForm 
                deck={deck}
                changeName={changeName}
                changeDescription={changeDescription}
                handleSubmit={handleSubmit}
            />
        </div>
    )
}
