import React, { useState, useEffect } from "react";
import { readDeck } from "../utils/api";
import { Link, useParams, useHistory } from "react-router-dom";

export default function Study() {
    const deckId = useParams();
    const history = useHistory();
    const [ deck, setDeck ] = useState({});
    const [ cards, setCards ] = useState({});
    const [ cardNumber, setCardNumber ] = useState(0);
    const [ flip, setFlip ] = useState(false);

    useEffect(() => {
        async function loadStudyDeck() {
            try {
                const result = await readDeck(deckId);
                setDeck(result);
                setCards(result.cards);
                console.log(result.cards)
            }catch (error){
                console.log("Aborted Study Deck")
            }
        }
        loadStudyDeck();
    }, [deckId]);

    function nextCard() {
        if(cardNumber + 1 < cards.length) {
            setCardNumber(cardNumber + 1);
            setFlip(false)
        }else{
            const result = window.confirm(`Restart the Deck?`)
            if(result){
                setCardNumber(0);
                setFlip(false)
            }else{
                history.pushState("/");
            }
        }
    }

    function displaySide() {
        return !flip ? cards[cardNumber].front : cards[cardNumber].back
        }

    return (
        <div>
              <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item">
                            <Link to={"/"} className="btn btn-link">Go Home</Link>
                        </li>
                        <li className="breadcrumb-item">
                            <Link to={`/decks/${deckId}`} className="btn btn-link">{deck.name}</Link>
                        </li>
                        <li className="breadcrumb-item active" aria-current="page">
                            Study
                        </li>
                    </ol>
                </nav>
                <h4>Study: {deck.name}</h4>
                <div className="card">
                    <div className="card-body">
                        <h5>Card {cardNumber + 1} of {cards.length}</h5>
                        <p className="card-text">{displaySide()}</p>
                        <button className="btn btn-primary" onClick={() => setFlip((front) => !front)}>
                            Flip
                        </button>
                        <button className="btn btn-secondary" onClick={nextCard}>
                            Next
                        </button>
                    </div>
                </div>
        </div>
    )
}
