import React, { useState, useEffect } from "react";
import { Link, useParams, useRouteMatch, useHistory } from "react-router-dom";
import { readDeck } from "../utils/api/index"
import CardList from "./CardList";

const NewDeck = () => {
    const { url } = useRouteMatch();
    const { deckId } = useParams();
    const history = useHistory();
    const [ deck, setDeck ] = useState({
        id: 0,
        name: "",
        cards: []
    });

    useEffect(() => {
        async function loadDeck() {
            try {
                const result = await readDeck(deckId);
                setDeck(result);
            }catch (error){
                console.log("Aborted Deck")
            }
        }
        loadDeck();
    }, [deckId]);

     const handleCardDelete = async ({ target }) => {
        const value = target.value;

        const result = window.confirm(`Delete this ${value} card? \n \nYou will not be able to recover it.`);
        if(result) {
            async function deleteThisCard() {
                try {
                    await deleteCard(value);
                    const valueFromAPI = await listCards(deckId);
                    setCards(valueFromAPI)
                }catch (error){
                    console.log("Aborted Card Delete")
                }
            }
        deleteThisCard();
        }
    };

    const deleteDeckHandler = (deckId) => {
        const result = window.confirm(`Delete this deck? \n \nYou will not be able to recover it.`);
        if(result) {
            async function deleteThisDeck() {
                try {
                    await deleteDeck(deckId);
                    history.push("/");
                }catch (error){
                    console.log("Aborted Deck Delete")
                }
            }
        deleteThisDeck();
        }
    };



    return (
        <div>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li class="breadcrumb-item">
              <Link to="/">
                <i class="bi bi-house-door"></i> Home
              </Link>
            </li>
            <li className="breadcrumb-item active" aria-current="page">
              {deck.name}
            </li>
          </ol>
        </nav>

        <div className="row">
          <div className="col">
            <div className="card">
              <div className="card-body">
                <h5>{deck.name}</h5>
                <p>{deck.description}</p>
                {/* This link takes you to EditDeck */}
                <div className="row">
                  <div className="col">
                    <Link to={`${url}/edit`}>
                      <button type="button" className="btn btn-info">
                        Edit
                      </button>
                    </Link>
                    <Link to={`${url}/study`}>
                      <button type="button" className="btn btn-info">
                          Study
                      </button>
                    </Link>
                    <Link to={`${url}/cards/new`}>
                      <button type="button" className="btn btn-info">
                          Add Card
                      </button>
                    </Link>
                  </div>
                  <div className="col text-right">
                    <Link to={`${url}decks/${deckId}`}>
                      <button
                        type="button"
                        onClick={deleteHandler}
                        className="btn btn-danger "
                      >
                      </button>
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="mb-5">
          <h3 className="p-3">Cards</h3>
          <CardList cards={deck.cards} />
        </div>
      </div>
    )
}

export default NewDeck;
